mapper.sql:
SELECT
COUNT(1)
FROM
admin_user u
WHERE 1 = 1

    AND u.username = #{user.username}


    AND d.dept_id = #{user.deptId}


    AND u.sex = #{user.sex}


    AND u.status = #{user.status}


    AND u.mobile = #{user.mobile}


    And u.created_at > #{user.createTimeFrom}


    And u.created_at < #{user.createTimeTo}

SELECT u.id userId, u.username, u.email, u.mobile, u.status, u.created_at createdAt, u.sex sex, u.dept_id deptId, u.last_login_time lastLoginTime, u.updated_at, u.description, u.avatar, d.dept_name deptName, GROUP_CONCAT(r.id) roleId, GROUP_CONCAT(r.role_name) roleName FROM admin_user u LEFT JOIN admin_dept d ON (u.dept_id = d.id) LEFT JOIN admin_user_role ur ON (u.id = ur.user_id) LEFT JOIN admin_role r ON r.id = ur.user_id WHERE 1 = 1
AND u.username = #{user.username}
AND d.id = #{user.deptId}
AND u.sex = #{user.sex}
AND u.status = #{user.status}
AND u.mobile = #{user.mobile}
And u.created_at > #{user.createTimeFrom}
And u.created_at < #{user.createTimeTo}
group by u.id
